mapper.update:
UPDATE evaluation AS e
SET e.result     = #{result},
    e.admin_id=#{adminId},
    e.update_time=CURRENT_TIMESTAMP
WHERE e.cid = #{cid}
  AND e.mid = #{mid}
  AND e.round = #{round}

mapper.select:
SELECT a.*
FROM admin AS a
WHERE a.username = #{username}

SELECT a.mname, a.mid, a.cname, a.cid, a.result
FROM admin_view_evaluation AS a
WHERE a.round = #{round};
