mapper.delete:
DELETE FROM major_favorite
WHERE stu_cid = {#cid}
  AND stu_sid = {#sid}

DELETE FROM tutor_favorite
WHERE stu_cid = {#cid}
  AND stu_sid = {#sid}

mapper.select:
SELECT uid, cid, sid, mid
FROM student_role
WHERE uid = #{uid}

SELECT cid, cname, mid, mname
FROM major_favor_message
WHERE stu_cid = #{stuCid}
  AND stu_sid = #{stuSid}

SELECT cid, cname, name, contact_phone, contact_email
FROM tutor_favor_message
WHERE stu_cid = #{stuCid}
  AND stu_sid = #{stuSid}
