mapper.update:
UPDATE user AS u
SET u.phone = #{phoneNumber}
WHERE u.uid = #{uid};

UPDATE user AS u
SET u.email = #{email}
WHERE u.uid = #{uid};

UPDATE user AS u
SET u.account_type = #{accountType}
WHERE u.uid = #{uid};

mapper.select:
SELECT u.*
FROM user AS u
WHERE u.uid = #{userId}

SELECT *
FROM not_verified_user AS n
WHERE n.uid = #{uid};

SELECT *
FROM user_stu AS u
WHERE u.uid = #{uid};

SELECT *
FROM user_teacher AS u
WHERE u.uid = #{uid};
